class Sudoku:
    def __init__(self):
      # self.puzzle = newPuzzle()
      self.puzzle = easyPuzzle()
      self.posX = 0
      self.posY = 0

    def setValue(self, value):
        if value < 0 or value > 9:
            return False

        self.puzzle[self.posX][self.posY]['value'] = value
        return value

    def move(self, direction):
        if direction == "UP":
            if self.posX > 0:
                self.posX -= 1
        elif direction == "RIGHT":
            if self.posY < 8:
                self.posY += 1
        elif direction == "DOWN":
            if self.posX < 8:
                self.posX += 1
        elif direction == "LEFT":
            if self.posY > 0:
                self.posY -= 1

    def print(self, printOptions):
        output = "====================================================================\n" if printOptions else "=========================================\n"

        for i in range(len(self.puzzle)):
            for j in range(len(self.puzzle[i])):
                output += "||" if j % 3 == 0 else "|"
                output += "=" if i == self.posX and j == self.posY else " "
                output += f"{self.puzzle[i][j]['value']}"
                output += f"({len(self.puzzle[i][j]['options'])})" if printOptions else ""
                output += "=" if i == self.posX and j == self.posY else " "

            output += "||\n"

            if i % 3 == 2:
                output += "====================================================================\n" if printOptions else "=========================================\n"
            else:
                output += "--------------------------------------------------------------------\n" if printOptions else "-----------------------------------------\n"

        print(output)
